class Solution:

    def isPalindromic(self, s: str, left, right):
        while left < right:
            if s[left] != s[right]:
                return False
            left += 1
            right -= 1
        return True

    def longestPalindrome(self, s: str) -> str:
        len_ = len(s)
        if len_ < 2:
            return s
        maxLen = 1
        begin = 0

        for i in range(0, len_ - 1):
            for j in range(i + 1, len_):
                if (j - i + 1 > maxLen) and (self.isPalindromic(s, i, j)):
                    maxLen = j - i + 1
                    begin = i
        print(begin, maxLen)
        return s[begin:begin + maxLen]


